import java.util.*;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.ConcurrentMap;
import java.util.concurrent.atomic.AtomicInteger;

class KeyValueStore {
    private final ConcurrentMap<String, Object> store = new ConcurrentHashMap<>();
    private final ConcurrentMap<String, AtomicInteger> accessCounts = new ConcurrentHashMap<>();
    private final ConcurrentMap<String, Long> lastAccessTimes = new ConcurrentHashMap<>();
    private final ConcurrentMap<Class<?>, AtomicInteger> typeCounts = new ConcurrentHashMap<>();

    public void put(String key, Object value) {
        if (key == null || value == null) {
            throw new IllegalArgumentException();
        }
        store.put(key, value);
        typeCounts.computeIfAbsent(value.getClass(), k -> new AtomicInteger(0)).incrementAndGet();
    }

    public Object get(String key) {
        if (key == null) {
            throw new IllegalArgumentException();
        }
        Object value = store.get(key);
        if (value != null) {
            accessCounts.computeIfAbsent(key, k -> new AtomicInteger(0)).incrementAndGet();
            lastAccessTimes.put(key, System.currentTimeMillis());
        }
        return value;
    }

    public int getAccessCount(String key) {
        if (key == null) {
            throw new IllegalArgumentException();
        }
        AtomicInteger count = accessCounts.get(key);
        return count == null ? 0 : count.get();
    }

    public long getLastAccessTime(String key) {
        if (key == null) {
            throw new IllegalArgumentException();
        }
        Long time = lastAccessTimes.get(key);
        return time == null ? 0L : time;
    }

    public int getTypeCount(Class<?> type) {
        if (type == null) {
            throw new IllegalArgumentException();
        }
        AtomicInteger count = typeCounts.get(type);
        return count == null ? 0 : count.get();
    }

    public void putAll(Map<String, Object> entries) {
        if (entries == null) {
            throw new IllegalArgumentException();
        }
        for (Map.Entry<String, Object> entry : entries.entrySet()) {
            put(entry.getKey(), entry.getValue());
        }
    }

    public Map<String, Object> computeStatistics() {
        Map<String, Object> stats = new HashMap<>();
        stats.put("totalEntries", store.size());

        String maxKey = null;
        int maxAccess = 0;
        for (Map.Entry<String, AtomicInteger> entry : accessCounts.entrySet()) {
            int count = entry.getValue().get();
            if (count > maxAccess) {
                maxAccess = count;
                maxKey = entry.getKey();
            }
        }
        stats.put("maxAccessKey", maxKey);

        Map<Class<?>, Integer> typeDistribution = new HashMap<>();
        for (Map.Entry<Class<?>, AtomicInteger> entry : typeCounts.entrySet()) {
            typeDistribution.put(entry.getKey(), entry.getValue().get());
        }
        stats.put("typeDistribution", typeDistribution);

        long estimatedMemory = 0;
        for (Map.Entry<String, Object> entry : store.entrySet()) {
            estimatedMemory += 40 + entry.getKey().length() * 2;
            Object value = entry.getValue();
            if (value instanceof String) {
                estimatedMemory += 40 + ((String) value).length() * 2;
            } else if (value instanceof Integer) {
                estimatedMemory += 16;
            } else if (value instanceof Long) {
                estimatedMemory += 24;
            } else {
                estimatedMemory += 32;
            }
        }
        stats.put("estimatedMemoryUsage", estimatedMemory);

        return stats;
    }

    public Set<String> findKeysByType(Class<?> type) {
        if (type == null) {
            throw new IllegalArgumentException();
        }
        Set<String> keys = new HashSet<>();
        for (Map.Entry<String, Object> entry : store.entrySet()) {
            if (type.isInstance(entry.getValue())) {
                keys.add(entry.getKey());
            }
        }
        return keys;
    }

    public void clear() {
        store.clear();
        accessCounts.clear();
        lastAccessTimes.clear();
        typeCounts.clear();
    }
}

