        kv.put("x", "hello");
        kv.put("y", 100);
        kv.get("x");
        kv.get("x");
        Map<String, Object> stats = kv.computeStatistics();
        assertEquals(2, stats.get("totalEntries"));
        assertEquals("x", stats.get("maxAccessKey"));
        assertTrue(((Map<?, ?>) stats.get("typeDistribution")).containsKey(String.class));
        assertTrue(((Map<?, ?>) stats.get("typeDistribution")).containsKey(Integer.class));
        assertTrue((Long) stats.get("estimatedMemoryUsage") > 0);
