import java.util.*;

class FactorySimulation {
    public static int[] taskFunc(int inputStock, int itemsToProcess, int factoryVersion) {
        int processed = 0;
        int remaining = inputStock;

        switch (factoryVersion) {
            case 1:
                // Version 1: 1 workshop that processes 1 item at a time
                processed = Math.min(inputStock, itemsToProcess);
                remaining = inputStock - processed;
                break;
            case 2:
                // Version 2: 2 workshops, each processes 1 item at a time
                int maxProcessV2 = Math.min(inputStock, itemsToProcess);
                processed = Math.min(maxProcessV2, 2); // Max 2 items per round
                remaining = inputStock - processed;
                break;
            case 3:
                // Version 3: 3 workshops, each processes 1 item at a time
                int maxProcessV3 = Math.min(inputStock, itemsToProcess);
                processed = Math.min(maxProcessV3, 3); // Max 3 items per round
                remaining = inputStock - processed;
                break;
            case 4:
                // Version 4: 4 workshops, each processes 1 item at a time
                int maxProcessV4 = Math.min(inputStock, itemsToProcess);
                processed = Math.min(maxProcessV4, 4); // Max 4 items per round
                remaining = inputStock - processed;
                break;
            default:
                // Fallback to version 1 behavior
                processed = Math.min(inputStock, itemsToProcess);
                remaining = inputStock - processed;
                break;
        }

        return new int[]{processed, remaining};
    }
}

