import java.util.*;

class FactorySimulation {
    private static int stock;
    private static int target;
    private static int processedCount;
    private static final Object lock = new Object();

    public static int[] taskFunc(int inputStock, int itemsToProcess, int factoryVersion) {
        stock = inputStock;
        target = itemsToProcess;
        processedCount = 0;

        int numWorkshops;
        switch (factoryVersion) {
            case 1: numWorkshops = 1; break;
            case 2: numWorkshops = 2; break;
            case 3: numWorkshops = 3; break;
            case 4: numWorkshops = 4; break;
            default: numWorkshops = 1; break;
        }

        List<Thread> threads = new ArrayList<>();
        for (int i = 0; i < numWorkshops; i++) {
            Thread t = new Thread(new Workshop());
            threads.add(t);
            t.start();
        }

        for (Thread t : threads) {
            try {
                t.join();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }

        return new int[]{processedCount, stock};
    }

    private static class Workshop implements Runnable {
        @Override
        public void run() {
            while (true) {
                synchronized (lock) {
                    if (processedCount >= target || stock <= 0) {
                        return;
                    }
                    stock--;
                    processedCount++;
                }
            }
        }
    }
}

