    void testConcurrentAccessToMessageHandlers() throws InterruptedException, NoSuchFieldException, IllegalAccessException {
        BiConsumer<String, String> logger = (a, b) -> {};
        WebSocketMessageHandler handler = new WebSocketMessageHandler(logger);
        int threads = 10;
        int iterations = 100;
        CountDownLatch latch = new CountDownLatch(threads);
        for (int i = 0; i < threads; i++) {
            final int id = i;
            new Thread(() -> {
                for (int j = 0; j < iterations; j++) {
                    handler.addMessageHandler("type" + id + "_" + j, data -> {});
                }
                latch.countDown();
            }).start();
        }
        latch.await();
        Field field = WebSocketMessageHandler.class.getDeclaredField("messageHandlers");
        field.setAccessible(true);
        Map<?, ?> map = (Map<?, ?>) field.get(handler);
        assertEquals(threads * iterations, map.size());
    }

    @Test
    void testRateLimiterThreadSafety() throws InterruptedException {
        BiConsumer<String, String> logger = (a, b) -> {};
        WebSocketMessageHandler handler = new WebSocketMessageHandler(logger);
        int threads = 20;
        int iterations = 50;
        CountDownLatch latch = new CountDownLatch(threads);
        AtomicInteger successCount = new AtomicInteger(0);
        for (int i = 0; i < threads; i++) {
            new Thread(() -> {
                for (int j = 0; j < iterations; j++) {
                    if (handler.processMessage("type", new HashMap<>())) {
                        successCount.incrementAndGet();
                    }
                }
                latch.countDown();
            }).start();
        }
        latch.await();
        assertTrue(successCount.get() <= 100);
    }
