import java.util.*;
import java.util.concurrent.*;
import java.util.function.*;

class WebSocketMessageHandler {
    private final Map<String, Consumer<Map<String, Object>>> messageHandlers;
    private final BiConsumer<String, String> errorLogger;
    private final RateLimiter rateLimiter;

    public WebSocketMessageHandler(BiConsumer<String, String> errorLogger) {
        this.messageHandlers = new ConcurrentHashMap<>();
        this.errorLogger = errorLogger;
        this.rateLimiter = new RateLimiter();
    }

    public void addMessageHandler(String messageType, Consumer<Map<String, Object>> handler) {
        if (messageType == null || handler == null) {
            throw new IllegalArgumentException("Message type and handler must not be null");
        }
        messageHandlers.put(messageType, handler);
    }

    public boolean processMessage(String messageType, Map<String, Object> messageData) {
        if (!rateLimiter.allowRequest()) {
            errorLogger.accept("RATE_LIMIT_EXCEEDED", "Rate limit exceeded for message type: " + messageType);
            return false;
        }
        if (messageType == null || messageType.isEmpty() || messageData == null) {
            errorLogger.accept("VALIDATION_FAILED", "Invalid message: messageType=" + messageType + ", messageData=" + messageData);
            return false;
        }
        Consumer<Map<String, Object>> handler = messageHandlers.get(messageType);
        if (handler != null) {
            try {
                handler.accept(messageData);
                return true;
            } catch (Exception e) {
                errorLogger.accept("HANDLER_EXCEPTION", "Exception in handler for message type " + messageType + ": " + e.getMessage());
                return false;
            }
        }
        return false;
    }

    public static class RateLimiter {
        private final Queue<Long> timestamps = new ConcurrentLinkedQueue<>();
        private static final long MAX_REQUESTS = 100;
        private static final long WINDOW_MS = 1000;

        public synchronized boolean allowRequest() {
            long now = System.currentTimeMillis();
            while (!timestamps.isEmpty() && now - timestamps.peek() > WINDOW_MS) {
                timestamps.poll();
            }
            if (timestamps.size() >= MAX_REQUESTS) {
                return false;
            }
            timestamps.offer(now);
            return true;
        }
    }
}

