        assertEquals("Added: 0, Removed: 0, Current: 0, Max: 0", q.getStats());
    }

    @Test
    void testGetStatsAfterPut() throws InterruptedException {
        EnhancedBlockingQueue<Integer> q = new EnhancedBlockingQueue<>(2);
        q.put(1);
        assertEquals("Added: 1, Removed: 0, Current: 1, Max: 1", q.getStats());
    }

    @Test
    void testGetStatsAfterTake() throws InterruptedException {
        EnhancedBlockingQueue<Integer> q = new EnhancedBlockingQueue<>(2);
        q.put(1);
        q.take();
        assertEquals("Added: 1, Removed: 1, Current: 0, Max: 1", q.getStats());
    }

    @Test
    void testGetStatsMaxSizeTracking() throws InterruptedException {
        EnhancedBlockingQueue<Integer> q = new EnhancedBlockingQueue<>(3);
        q.put(1);
        q.put(2);
        q.put(3);
        assertEquals("Added: 3, Removed: 0, Current: 3, Max: 3", q.getStats());
        q.take();
        q.put(4);
        assertEquals("Added: 4, Removed: 1, Current: 3, Max: 3", q.getStats());
    }

