import java.util.*;

class EnhancedOptions {
    private Map<String, Object> options;
    private Set<String> requiredOptions;
    private Map<String, Object> defaultOptions;
    private Map<String, String> optionDependencies;

    public EnhancedOptions() {
        options = new HashMap<>();
        requiredOptions = new HashSet<>(Arrays.asList("backend", "doctype"));
        defaultOptions = new HashMap<>();
        defaultOptions.put("header_footer", true);
        defaultOptions.put("compact", false);
        defaultOptions.put("safe", 0);
        optionDependencies = new HashMap<>();
        optionDependencies.put("template_engine", "template_dirs");

        // Initialize options with default values
        options.putAll(defaultOptions);
    }

    public void setOption(String key, Object value) {
        if (value == null) {
            throw new IllegalArgumentException("Option value cannot be null");
        }
        validateOption(key, value);
        options.put(key, value);
    }

    public Object getOption(String key) {
        if (!options.containsKey(key)) {
            return defaultOptions.get(key);
        }
        return options.get(key);
    }

    private void validateOption(String key, Object value) {
        switch (key) {
            case "safe":
                if (!(value instanceof Integer)) {
                    throw new IllegalArgumentException("Option 'safe' must be an Integer");
                }
                int safeValue = (Integer) value;
                if (safeValue < 0 || safeValue > 3) {
                    throw new IllegalArgumentException("Option 'safe' must be between 0 and 3");
                }
                break;
            case "template_dirs":
                if (!(value instanceof List) && !(value instanceof String[])) {
                    throw new IllegalArgumentException("Option 'template_dirs' must be a List or String array");
                }
                break;
            case "backend":
                if (!(value instanceof String)) {
                    throw new IllegalArgumentException("Option 'backend' must be a String");
                }
                break;
            case "template_engine":
                String dependency = optionDependencies.get(key);
                if (dependency != null && !options.containsKey(dependency)) {
                    throw new IllegalArgumentException("Cannot set 'template_engine' because 'template_dirs' has not been set.");
                }
                break;
        }
    }

    public void validateAllOptions() {
        for (String required : requiredOptions) {
            if (!options.containsKey(required)) {
                throw new IllegalStateException("Required option '" + required + "' is missing");
            }
        }
    }

    public Map<String, Object> getAllOptions() {
        Map<String, Object> allOptions = new HashMap<>();
        allOptions.putAll(defaultOptions);
        allOptions.putAll(options);
        return Collections.unmodifiableMap(allOptions);
    }

    public void merge(EnhancedOptions other) {
        if (other == null) {
            throw new IllegalArgumentException("Cannot merge with null EnhancedOptions");
        }
        Map<String, Object> otherOptions = other.getAllOptions();
        for (Map.Entry<String, Object> entry : otherOptions.entrySet()) {
            setOption(entry.getKey(), entry.getValue());
        }
    }
}

