import java.util.HashMap;
import java.util.concurrent.CompletableFuture;
import java.util.function.Consumer;

class AsyncResponseHandler {
    private final HashMap<String, Consumer<Object>> callbacks;
    private Consumer<Exception> errorHandler;

    public AsyncResponseHandler() {
        this.callbacks = new HashMap<>();
    }

    public void registerCallback(String responseType, Consumer<Object> callback) {
        callbacks.put(responseType, callback);
    }

    public void registerErrorHandler(Consumer<Exception> handler) {
        this.errorHandler = handler;
    }

    public void processResponse(String responseType, Object response) {
        if (callbacks.containsKey(responseType)) {
            callbacks.get(responseType).accept(response);
        } else {
            if (errorHandler != null) {
                errorHandler.accept(new IllegalArgumentException("Missing handler for response type: " + responseType));
            }
        }
    }

    public void executeAsync(Runnable task, String responseType) {
        CompletableFuture.runAsync(task)
            .thenRun(() -> processResponse(responseType, "Success"))
            .exceptionally(ex -> {
                if (errorHandler != null) {
                    Throwable cause = ex.getCause();
                    if (cause instanceof Exception) {
                        errorHandler.accept((Exception) cause);
                    } else {
                        errorHandler.accept(new RuntimeException(cause));
                    }
                }
                return null;
            });
    }
}

