import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.*;
import java.util.concurrent.atomic.AtomicInteger;

class TestCases {
    @Test
    void testConcurrentRegistrationRaceCondition() throws InterruptedException {
        PlayerManager manager = new PlayerManager();
        int threadCount = 100;
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        CountDownLatch startLatch = new CountDownLatch(1);
        CountDownLatch endLatch = new CountDownLatch(threadCount);
        AtomicInteger successCount = new AtomicInteger(0);

        for (int i = 0; i < threadCount; i++) {
            executor.submit(() -> {
                try {
                    startLatch.await(); // Ensure all threads start at the same time
                    boolean result = manager.registerPlayer("racePlayer");
                    if (result) {
                        successCount.incrementAndGet();
                    }
                } catch (InterruptedException ignored) {
                } finally {
                    endLatch.countDown();
                }
            });
        }

        startLatch.countDown(); // Release all threads
        endLatch.await();
        executor.shutdown();

        // Exactly one thread should succeed
        assertEquals(1, successCount.get());
        assertEquals(1, manager.getPlayers().size()); // Only one player registered
    }

    @Test
    void testConcurrentRegistrationOfDifferentPlayers() throws InterruptedException {
        PlayerManager manager = new PlayerManager();
        int threadCount = 50;
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        CountDownLatch endLatch = new CountDownLatch(threadCount);

        for (int i = 0; i < threadCount; i++) {
            final String playerId = "player" + i;
            executor.submit(() -> {
                try {
                    manager.registerPlayer(playerId);
                } finally {
                    endLatch.countDown();
                }
            });
        }

        endLatch.await();
        executor.shutdown();

        // All registrations should succeed
        assertEquals(threadCount, manager.getPlayers().size());
    }
}

