import java.util.*;
import java.util.concurrent.ConcurrentLinkedQueue;
import java.util.concurrent.CopyOnWriteArrayList;
import java.io.OutputStream;

class PlayerInfo {
    private final String playerId;
    private int score;
    private boolean active;

    public PlayerInfo(String playerId) {
        this.playerId = playerId;
        this.score = 0;
        this.active = true;
    }

    public String getPlayerId() {
        return playerId;
    }

    public int getScore() {
        return score;
    }

    public boolean isActive() {
        return active;
    }

    public void setActive(boolean active) {
        this.active = active;
    }

    public void addScore(int delta) {
        this.score += delta;
    }
}

class PlayerManager {
    private final Map<String, PlayerInfo> players = new ConcurrentHashMap<>();
    private final ConcurrentLinkedQueue<String> queue = new ConcurrentLinkedQueue<>();
    private final List<OutputStream> outputStreams = new CopyOnWriteArrayList<>();

    public boolean registerPlayer(String playerId) {
        if (players.putIfAbsent(playerId, new PlayerInfo(playerId)) == null) {
            queue.add(playerId);
            return true;
        }
        return false;
    }

    public Map<String, PlayerInfo> getPlayers() {
        return players;
    }

    public boolean removePlayer(String playerId) {
        PlayerInfo info = players.get(playerId);
        if (info != null && info.isActive()) {
            info.setActive(false);
            queue.remove(playerId);
            return true;
        }
        return false;
    }

    public boolean updateScore(String playerId, int delta) {
        PlayerInfo info = players.get(playerId);
        if (info != null && info.isActive()) {
            info.addScore(delta);
            return true;
        }
        return false;
    }

    public ConcurrentLinkedQueue<String> getQueue() {
        return queue;
    }

    public String getNextPlayer() {
        String playerId = queue.poll();
        if (playerId != null) {
            queue.add(playerId);
        }
        return playerId;
    }

    public List<PlayerInfo> getRankings() {
        List<PlayerInfo> activePlayers = new ArrayList<>();
        for (PlayerInfo info : players.values()) {
            if (info.isActive()) {
                activePlayers.add(info);
            }
        }
        activePlayers.sort((a, b) -> Integer.compare(b.getScore(), a.getScore()));
        return activePlayers;
    }

    public void addOutputStream(OutputStream stream) {
        if (stream != null && !outputStreams.contains(stream)) {
            outputStreams.add(stream);
        }
    }

    public void removeOutputStream(OutputStream stream) {
        outputStreams.remove(stream);
    }

    public List<OutputStream> getOutputStreams() {
        return new ArrayList<>(outputStreams);
    }
}

public class task_func {
    public static void main(String[] args) {
        PlayerManager manager = new PlayerManager();
        System.out.println(manager.registerPlayer("player1")); // true
        System.out.println(manager.registerPlayer("player1")); // false
    }
}

