import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.io.ByteArrayOutputStream;
import java.util.List;

class TestCases {
    @Test
    void testGetRankingsReturnsActivePlayersSortedByScoreDescending() {
        PlayerManager manager = new PlayerManager();
        manager.registerPlayer("p1");
        manager.registerPlayer("p2");
        manager.registerPlayer("p3");
        manager.updateScore("p1", 10);
        manager.updateScore("p2", 30);
        manager.updateScore("p3", 20);
        manager.removePlayer("p3");
        List<PlayerInfo> rankings = manager.getRankings();
        assertEquals(2, rankings.size());
        assertEquals("p2", rankings.get(0).getPlayerId());
        assertEquals(30, rankings.get(0).getScore());
        assertEquals("p1", rankings.get(1).getPlayerId());
        assertEquals(10, rankings.get(1).getScore());
    }

    @Test
    void testAddOutputStreamAddsUniqueStreams() {
        PlayerManager manager = new PlayerManager();
        ByteArrayOutputStream os1 = new ByteArrayOutputStream();
        ByteArrayOutputStream os2 = new ByteArrayOutputStream();
        manager.addOutputStream(os1);
        manager.addOutputStream(os1);
        manager.addOutputStream(os2);
        List<OutputStream> streams = manager.getOutputStreams();
        assertEquals(2, streams.size());
        assertTrue(streams.contains(os1));
        assertTrue(streams.contains(os2));
    }

    @Test
    void testRemoveOutputStreamRemovesStream() {
        PlayerManager manager = new PlayerManager();
        ByteArrayOutputStream os = new ByteArrayOutputStream();
        manager.addOutputStream(os);
        manager.removeOutputStream(os);
        List<OutputStream> streams = manager.getOutputStreams();
        assertEquals(0, streams.size());
    }

    @Test
    void testGetOutputStreamsReturnsCopy() {
        PlayerManager manager = new PlayerManager();
        ByteArrayOutputStream os = new ByteArrayOutputStream();
        manager.addOutputStream(os);
        List<OutputStream> streams = manager.getOutputStreams();
        streams.clear();
        assertEquals(1, manager.getOutputStreams().size());
    }
}

