                new TransformerMetadata("ContactReportToXml", "1.0", "Transforms contact reports from TXT to XML format"));

        registerTransformer("opnoteToXml",
                new TransformerDescriptor("opnote", TransformerType.TXT2XML),
                new TransformerMetadata("OpNoteToXml", "1.0", "Transforms operational notes from TXT to XML format"));

        registerTransformer("fourwhiskyToXml",
                new TransformerDescriptor("fourwhisky", TransformerType.TXT2XML),
                new TransformerMetadata("FourWhiskyToXml", "1.0", "Transforms fourwhisky data from TXT to XML format"));

        registerTransformer("contactreportToTxt",
                new TransformerDescriptor("contactreport", TransformerType.XML2TXT),
                new TransformerMetadata("ContactReportToTxt", "1.0", "Transforms contact reports from XML to TXT format"));

        registerTransformer("opnoteToTxt",
                new TransformerDescriptor("opnote", TransformerType.XML2TXT),
