import java.util.*;

enum HitResult {
    HIT, MISS, SUNK
}

class GameState {
    public final int remainingShips;
    public final Set<String> attacksMade;
    public final boolean gameOver;
    public final String winner;

    public GameState(int remainingShips, Set<String> attacksMade, boolean gameOver, String winner) {
        this.remainingShips = remainingShips;
        this.attacksMade = attacksMade;
        this.gameOver = gameOver;
        this.winner = winner;
    }
}

class StateChange {
    public final String eventType;
    public final GameState gameState;

    public StateChange(String eventType, GameState gameState) {
        this.eventType = eventType;
        this.gameState = gameState;
    }
}

class NavalBattleAdapter extends Observable {
    private Set<String> shipPositions;
    private Set<String> attackPositions;
    private boolean gameOver;
    private String winner;

    public NavalBattleAdapter() {
        this.shipPositions = new HashSet<>();
        this.attackPositions = new HashSet<>();
        this.gameOver = false;
        this.winner = null;
    }

    public void placeShips(List<String> coordinates) {
        if (coordinates == null || coordinates.isEmpty()) {
            throw new IllegalArgumentException();
        }
        shipPositions.addAll(coordinates);
        notifyStateChange("SHIPS_PLACED");
    }

    public HitResult receiveAttack(String coordinate) {
        if (coordinate == null || coordinate.isEmpty()) {
            throw new IllegalArgumentException();
        }
        attackPositions.add(coordinate);
        boolean hit = shipPositions.contains(coordinate);
        if (hit) {
            shipPositions.remove(coordinate);
            notifyStateChange("HIT");
            if (shipPositions.isEmpty()) {
                gameOver = true;
                winner = "Opponent";
                notifyStateChange("GAME_OVER");
                return HitResult.SUNK;
            }
            return HitResult.HIT;
        }
        notifyStateChange("MISS");
        return HitResult.MISS;
    }

    public GameState getGameState() {
        return new GameState(shipPositions.size(), new HashSet<>(attackPositions), gameOver, winner);
    }

    private void notifyStateChange(String eventType) {
        setChanged();
        notifyObservers(new StateChange(eventType, getGameState()));
    }
}

