import java.util.*;

class AdditionChainFinder {
    public List<Integer> findShortestAdditionChain(int target) {
        if (target == 1) {
            return Arrays.asList(1);
        }

        // BFS to find shortest addition chain
        Queue<List<Integer>> queue = new LinkedList<>();
        Set<Integer> visited = new HashSet<>();

        queue.offer(Arrays.asList(1));
        visited.add(1);

        while (!queue.isEmpty()) {
            List<Integer> chain = queue.poll();
            int lastValue = chain.get(chain.size() - 1);

            if (lastValue == target) {
                return chain;
            }

            // Try all possible sums of two numbers in the current chain
            for (int i = 0; i < chain.size(); i++) {
                for (int j = i; j < chain.size(); j++) {
                    int sum = chain.get(i) + chain.get(j);

                    if (sum <= target && !visited.contains(sum)) {
                        visited.add(sum);
                        List<Integer> newChain = new ArrayList<>(chain);
                        newChain.add(sum);
                        queue.offer(newChain);
                    }
                }
            }
        }

        return new ArrayList<>(); // Should not reach here for valid targets
    }
}

