import java.util.*;

class AdditionChainFinder {
    public List<Integer> findShortestAdditionChain(int target) {
        if (target < 1) {
            throw new IllegalArgumentException("Target number must be at least 1, but was: " + target);
        }

        if (target == 1) {
            return Arrays.asList(1);
        }

        // DFS to find shortest addition chain
        List<Integer> shortestChain = null;
        int minLength = Integer.MAX_VALUE;

        // Use DFS with a stack-based approach
        Stack<List<Integer>> stack = new Stack<>();
        stack.push(Arrays.asList(1));

        while (!stack.isEmpty()) {
            List<Integer> chain = stack.pop();
            int lastValue = chain.get(chain.size() - 1);

            if (lastValue == target) {
                if (chain.size() < minLength || 
                    (chain.size() == minLength && (shortestChain == null || isLexicographicallySmaller(chain, shortestChain)))) {
                    shortestChain = new ArrayList<>(chain);
                    minLength = chain.size();
                }
                continue;
            }

            // Pruning: if current chain is already longer than or equal to the shortest found, skip
            if (chain.size() >= minLength) {
                continue;
            }

            // Try all possible sums of two numbers in the current chain
            // Process in reverse order to ensure lexicographically smaller chains are found first
            List<Integer> possibleSums = new ArrayList<>();
            for (int i = 0; i < chain.size(); i++) {
                for (int j = i; j < chain.size(); j++) {
                    int sum = chain.get(i) + chain.get(j);
                    if (sum <= target && !chain.contains(sum)) {
                        possibleSums.add(sum);
                    }
                }
            }

            // Sort in descending order for stack (so smaller values are processed first)
            Collections.sort(possibleSums, Collections.reverseOrder());

            for (int sum : possibleSums) {
                List<Integer> newChain = new ArrayList<>(chain);
                newChain.add(sum);
                stack.push(newChain);
            }
        }

        return shortestChain != null ? shortestChain : new ArrayList<>();
    }

    private boolean isLexicographicallySmaller(List<Integer> chain1, List<Integer> chain2) {
        for (int i = 0; i < Math.min(chain1.size(), chain2.size()); i++) {
            if (chain1.get(i) < chain2.get(i)) {
                return true;
            } else if (chain1.get(i) > chain2.get(i)) {
                return false;
            }
        }
        return chain1.size() < chain2.size();
    }
}

