import java.util.*;
import java.util.concurrent.*;

class ThreadManager {
    public static class ThreadStatistics {
        private final int totalThreads;
        private final int completedTasks;
        private final int interruptedTasks;

        public ThreadStatistics(int totalThreads, int completedTasks, int interruptedTasks) {
            this.totalThreads = totalThreads;
            this.completedTasks = completedTasks;
            this.interruptedTasks = interruptedTasks;
        }

        public int getTotalThreads() {
            return totalThreads;
        }

        public int getCompletedTasks() {
            return completedTasks;
        }

        public int getInterruptedTasks() {
            return interruptedTasks;
        }
    }

    public ThreadStatistics manageThreads(int threadCount, long workDurationMs, long interruptAfterMs) {
        List<Thread> threads = new ArrayList<>();

        // Create and start worker threads
        for (int i = 0; i < threadCount; i++) {
            Thread worker = new Thread(() -> {
                System.out.println(Thread.currentThread().getName());
                try {
                    Thread.sleep(workDurationMs);
                } catch (InterruptedException e) {
                    // Thread was interrupted
                }
            });
            threads.add(worker);
            worker.start();
        }

        // Wait for all threads to complete
        for (Thread thread : threads) {
            try {
                thread.join();
            } catch (InterruptedException e) {
                // Handle interruption during join
            }
        }

        // Return statistics with total thread count and zero for completed/interrupted
        return new ThreadStatistics(threadCount, 0, 0);
    }
}

