import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.CyclicBarrier;
import java.util.concurrent.atomic.AtomicInteger;
import java.util.List;
import java.util.ArrayList;
import java.util.Collections;

class TestThreadManager {

    @Test
    public void testAtomicVariablesPreventRaceConditions() throws Exception {
        // This test creates a scenario that would fail without atomic variables
        // by having multiple threads concurrently access shared counters

        final int CONCURRENT_MANAGERS = 50;
        final int THREADS_PER_MANAGER = 10;
        CountDownLatch startLatch = new CountDownLatch(1);
        CountDownLatch endLatch = new CountDownLatch(CONCURRENT_MANAGERS);
        List<Integer> totalCounts = Collections.synchronizedList(new ArrayList<>());

        for (int i = 0; i < CONCURRENT_MANAGERS; i++) {
            new Thread(() -> {
                try {
                    startLatch.await();
                    ThreadManager manager = new ThreadManager();
                    ThreadManager.ThreadStatistics stats = manager.manageThreads(THREADS_PER_MANAGER, 20, 10);

                    // Without atomic variables, this sum could be inconsistent due to race conditions
                    int total = stats.getCompletedTasks() + stats.getInterruptedTasks();
                    totalCounts.add(total);
                    endLatch.countDown();
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
            }).start();
        }

        startLatch.countDown();
        endLatch.await();

        // With atomic variables, every manager should account for all its threads
        for (int i = 0; i < totalCounts.size(); i++) {
            assertEquals(THREADS_PER_MANAGER, totalCounts.get(i).intValue(), 
                "Race condition detected at manager " + i + " - atomic variables required for thread safety");
        }
    }

    @Test
    public void testIsAliveCheckPreventsUnnecessaryInterrupts() throws Exception {
        // Create a custom ThreadManager that tracks interrupt attempts
        InterruptTrackingThreadManager manager = new InterruptTrackingThreadManager();

        // Use very short work duration so threads complete before interrupt
        ThreadManager.ThreadStatistics stats = manager.manageThreads(5, 1, 50);

        // All threads should complete before interrupt time
        assertEquals(5, stats.getCompletedTasks());
        assertEquals(0, stats.getInterruptedTasks());

        // The key test: without isAlive() check, all 5 threads would be interrupted
        // With isAlive() check, no interrupts should be attempted on dead threads
        assertTrue(manager.getInterruptAttempts() == 0, 
            "isAlive() check should prevent interrupting dead threads. Attempts: " + manager.getInterruptAttempts());
    }

    @Test
    public void testAtomicBooleanCoordinatesInterruptState() throws Exception {
        // Test that specifically requires AtomicBoolean for interrupt coordination
        final int ITERATIONS = 100;
        List<Boolean> consistencyResults = Collections.synchronizedList(new ArrayList<>());
        CountDownLatch allDone = new CountDownLatch(ITERATIONS);

        for (int i = 0; i < ITERATIONS; i++) {
            new Thread(() -> {
                ThreadManager manager = new ThreadManager();
                ThreadManager.ThreadStatistics stats = manager.manageThreads(3, 25, 15);

                // Without AtomicBoolean, interrupt state coordination could be inconsistent
                boolean consistent = (stats.getCompletedTasks() + stats.getInterruptedTasks()) == 3;
                consistencyResults.add(consistent);
                allDone.countDown();
            }).start();
        }

        allDone.await();

        // All iterations should be consistent with atomic variables
        long inconsistentCount = consistencyResults.stream().mapToInt(b -> b ? 0 : 1).sum();
        assertEquals(0, inconsistentCount, 
            "AtomicBoolean required for consistent interrupt coordination. Inconsistent results: " + inconsistentCount);
    }

    @Test
    public void testConcurrentAtomicOperations() throws Exception {
        // This test would fail with regular int variables due to lost updates
        final int THREAD_COUNT = 100;
        CyclicBarrier barrier = new CyclicBarrier(THREAD_COUNT);
        AtomicInteger successCount = new AtomicInteger(0);

        for (int i = 0; i < THREAD_COUNT; i++) {
            new Thread(() -> {
                try {
                    barrier.await(); // Synchronize start to maximize contention
                    ThreadManager manager = new ThreadManager();
                    ThreadManager.ThreadStatistics stats = manager.manageThreads(2, 10, 5);

                    // This operation requires atomic counters to be accurate
                    if (stats.getCompletedTasks() + stats.getInterruptedTasks() == 2) {
                        successCount.incrementAndGet();
                    }
                } catch (Exception e) {
                    // Ignore exceptions for this test
                }
            }).start();
        }

        Thread.sleep(2000); // Wait for all threads to complete

        // Without atomic variables, many operations would have inconsistent results
        assertTrue(successCount.get() >= THREAD_COUNT * 0.95, 
            "Atomic variables required for concurrent operations. Success rate: " + 
            (successCount.get() * 100.0 / THREAD_COUNT) + "%");
    }

    // Helper class to track interrupt attempts
    private static class InterruptTrackingThreadManager extends ThreadManager {
        private volatile int interruptAttempts = 0;

        @Override
        public ThreadStatistics manageThreads(int threadCount, long workDurationMs, long interruptAfterMs) {
            List<Thread> threads = new ArrayList<>();
            AtomicInteger completedTasks = new AtomicInteger(0);
            AtomicInteger interruptedTasks = new AtomicInteger(0);
            AtomicBoolean isInterrupted = new AtomicBoolean(false);

            // Create and start worker threads
            for (int i = 0; i < threadCount; i++) {
                Thread worker = new Thread(() -> {
                    System.out.println(Thread.currentThread().getName());
                    try {
                        Thread.sleep(workDurationMs);
                        completedTasks.incrementAndGet();
                    } catch (InterruptedException e) {
                        interruptedTasks.incrementAndGet();
                    }
                });
                threads.add(worker);
                worker.start();
            }

            // Start interrupt thread
            Thread interruptThread = new Thread(() -> {
                try {
                    Thread.sleep(interruptAfterMs);
                    isInterrupted.set(true);
                    for (Thread thread : threads) {
                        if (thread.isAlive()) {
                            interruptAttempts++;
                            thread.interrupt();
                        }
                    }
                } catch (InterruptedException e) {
                    // Interrupt thread was interrupted
                }
            });
            interruptThread.start();

            // Wait for all threads to complete
            for (Thread thread : threads) {
                try {
                    thread.join();
                } catch (InterruptedException e) {
                    // Handle interruption during join
                }
            }

            try {
                interruptThread.join();
            } catch (InterruptedException e) {
                // Handle interruption during join
            }

            return new ThreadStatistics(threadCount, completedTasks.get(), interruptedTasks.get());
        }

        public int getInterruptAttempts() {
            return interruptAttempts;
        }
    }
}

