import java.util.*;

class TreeNode {
    int val;
    TreeNode left;
    TreeNode right;

    TreeNode() {}
    TreeNode(int val) { this.val = val; }
    TreeNode(int val, TreeNode left, TreeNode right) {
        this.val = val;
        this.left = left;
        this.right = right;
    }
}

class TreeAnalyzer {
    public Map<String, Integer> analyzeTree(TreeNode root) {
        Map<String, Integer> result = new HashMap<>();
        result.put("nodeCount", countNodes(root));
        result.put("maxDiff", findMaxDiff(root));
        return result;
    }

    private int countNodes(TreeNode node) {
        if (node == null) {
            return 0;
        }
        return 1 + countNodes(node.left) + countNodes(node.right);
    }

    private int findMaxDiff(TreeNode root) {
        if (root == null) {
            return 0;
        }
        return findMaxDiffHelper(root, root.val, root.val);
    }

    private int findMaxDiffHelper(TreeNode node, int minAncestor, int maxAncestor) {
        if (node == null) {
            return maxAncestor - minAncestor;
        }

        int currentDiff = Math.max(Math.abs(node.val - minAncestor), Math.abs(node.val - maxAncestor));
        int newMin = Math.min(minAncestor, node.val);
        int newMax = Math.max(maxAncestor, node.val);

        int leftDiff = findMaxDiffHelper(node.left, newMin, newMax);
        int rightDiff = findMaxDiffHelper(node.right, newMin, newMax);

        return Math.max(currentDiff, Math.max(leftDiff, rightDiff));
    }
}

