            this.maxDiff = 0;
            this.minDiff = Integer.MAX_VALUE;
            this.sumDiff = 0;
        }
    }

    public Map<String, Integer> analyzeTree(TreeNode root) {
        Map<String, Integer> result = new HashMap<>();

        if (root == null) {
            result.put("nodeCount", 0);
            result.put("maxDiff", 0);
            result.put("minDiff", 0);
            result.put("avgDiff", 0);
            return result;
        }

        TraversalResult traversalResult = new TraversalResult();
        singleTraversal(root, root.val, root.val, traversalResult);

        result.put("nodeCount", traversalResult.nodeCount);
        result.put("maxDiff", traversalResult.maxDiff);
        result.put("minDiff", traversalResult.minDiff == Integer.MAX_VALUE ? 0 : traversalResult.minDiff);
        result.put("avgDiff", (int) Math.floor((double) traversalResult.sumDiff / traversalResult.nodeCount));

        return result;
    }

    private void singleTraversal(TreeNode node, int minAncestor, int maxAncestor, TraversalResult result) {
        if (node == null) {
            return;
        }

        // Count this node
        result.nodeCount++;

        // Calculate differences with ancestors
        int diffWithMin = Math.abs(node.val - minAncestor);
        int diffWithMax = Math.abs(node.val - maxAncestor);

        // Update max difference
        result.maxDiff = Math.max(result.maxDiff, Math.max(diffWithMin, diffWithMax));

        // Update min difference (only if there are actual ancestors)
        if (minAncestor != node.val) {
            result.minDiff = Math.min(result.minDiff, diffWithMin);
        }
        if (maxAncestor != node.val) {
            result.minDiff = Math.min(result.minDiff, diffWithMax);
        }

        // Add to sum of differences
        result.sumDiff += diffWithMin + diffWithMax;

        // Update ancestor bounds
        int newMin = Math.min(minAncestor, node.val);
        int newMax = Math.max(maxAncestor, node.val);

        // Traverse children
        singleTraversal(node.left, newMin, newMax, result);
        singleTraversal(node.right, newMin, newMax, result);
    }
}

