import java.util.Objects;

class BinaryTreeNextNode {
    static class BinaryTreeNode {
        int value;
        BinaryTreeNode left;
        BinaryTreeNode right;
        BinaryTreeNode parent;

        public BinaryTreeNode(int value) {
            this.value = value;
        }

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (o == null || getClass() != o.getClass()) return false;
            BinaryTreeNode that = (BinaryTreeNode) o;
            return value == that.value;
        }

        @Override
        public int hashCode() {
            return Objects.hash(value);
        }
    }

    public static BinaryTreeNode findNextNode(BinaryTreeNode node) {
        if (node == null) {
            throw new IllegalArgumentException("Input node cannot be null");
        }

        if (node.right != null) {
            BinaryTreeNode current = node.right;
            while (current.left != null) {
                current = current.left;
            }
            return current;
        }

        // Node has no right child - find first ancestor where node is in left subtree
        BinaryTreeNode current = node;
        BinaryTreeNode parent = current.parent;

        while (parent != null && current == parent.right) {
            current = parent;
            parent = parent.parent;
        }

        return parent;
    }

    public static BinaryTreeNode buildTree(BinaryTreeNode[] nodes, int[][] relationships) {
        if (nodes == null || nodes.length == 0) {
            throw new IllegalArgumentException("Invalid input: nodes array must be non-null and non-empty, and all indices in relationships must be valid.");
        }

        for (int[] relationship : relationships) {
            int parentIndex = relationship[0];
            int childIndex = relationship[1];
            int direction = relationship[2]; // 'L' or 'R' as int

            if (parentIndex < 0 || parentIndex >= nodes.length || 
                childIndex < 0 || childIndex >= nodes.length) {
                throw new IllegalArgumentException("Invalid input: nodes array must be non-null and non-empty, and all indices in relationships must be valid.");
            }

            BinaryTreeNode parent = nodes[parentIndex];
            BinaryTreeNode child = nodes[childIndex];

            if (direction == 'L') {
                parent.left = child;
            } else if (direction == 'R') {
                parent.right = child;
            }

            child.parent = parent;
        }

        return nodes[0];
    }
}

