import java.util.*;

class GraphAnalyzer {
    public static Map<String, Object> analyzeGraph(int[][] graph, int source) {
        int n = graph.length;
        int[] distances = new int[n];
        int[] prev = new int[n];
        boolean[] visited = new boolean[n];

        // Initialize distances and previous array
        Arrays.fill(distances, Integer.MAX_VALUE);
        Arrays.fill(prev, -1);
        distances[source] = 0;

        // Priority queue to store vertices by distance
        PriorityQueue<Vertex> pq = new PriorityQueue<>((a, b) -> Integer.compare(a.distance, b.distance));
        pq.offer(new Vertex(source, 0));

        while (!pq.isEmpty()) {
            Vertex current = pq.poll();
            int u = current.index;

            if (visited[u]) continue;
            visited[u] = true;

            // Check all neighbors
            for (int v = 0; v < n; v++) {
                if (graph[u][v] > 0 && !visited[v]) {
                    int newDist = distances[u] + graph[u][v];
                    if (newDist < distances[v]) {
                        distances[v] = newDist;
                        prev[v] = u;
                        pq.offer(new Vertex(v, newDist));
                    }
                }
            }
        }

        // Build result map
        Map<String, Object> result = new HashMap<>();
        List<Integer> distancesList = new ArrayList<>();
        for (int dist : distances) {
            distancesList.add(dist);
        }
        result.put("distances", distancesList);

        // Build paths for all vertices
        Map<Integer, List<Integer>> paths = new HashMap<>();
        for (int i = 0; i < n; i++) {
            paths.put(i, buildPath(prev, source, i));
        }
        result.put("paths", paths);

        return result;
    }

    private static List<Integer> buildPath(int[] prev, int source, int target) {
        List<Integer> path = new ArrayList<>();

        // If target is unreachable, return empty path
        if (prev[target] == -1 && target != source) {
            return path;
        }

        // Build path backwards
        int current = target;
        while (current != -1) {
            path.add(0, current);
            current = prev[current];
        }

        return path;
    }

    private static class Vertex {
        int index;
        int distance;

        Vertex(int index, int distance) {
            this.index = index;
            this.distance = distance;
        }
    }
}

