import java.util.HashMap;
import java.util.Map;

class FileUploadUtil {

    public static String getFilename(String contentDisposition) {
        if (contentDisposition == null || contentDisposition.isEmpty()) {
            return null;
        }

        // Try filename* first (RFC 5987)
        String filenameStar = extractFilename(contentDisposition, "filename*=UTF-8''");
        if (filenameStar != null) {
            return decodeRFC5987(filenameStar);
        }

        // Try filename* without encoding prefix
        filenameStar = extractFilename(contentDisposition, "filename*=");
        if (filenameStar != null) {
            return filenameStar;
        }

        // Try filename
        return extractFilename(contentDisposition, "filename=");
    }

    private static String extractFilename(String contentDisposition, String prefix) {
        int index = contentDisposition.indexOf(prefix);

        if (index == -1) {
            return null;
        }

        // Start after prefix
        int start = index + prefix.length();

        // Check if the filename is quoted
        if (start < contentDisposition.length() && contentDisposition.charAt(start) == '"') {
            start++; // Skip the opening quote
            int end = contentDisposition.indexOf('"', start);
            if (end != -1) {
                return contentDisposition.substring(start, end);
            }
        } else if (start < contentDisposition.length() && contentDisposition.charAt(start) == '\'') {
            start++; // Skip the opening single quote
            int end = contentDisposition.indexOf('\'', start);
            if (end != -1) {
                return contentDisposition.substring(start, end);
            }
        } else {
            // Filename is not quoted, find the next semicolon or end of string
            // We can't use space as delimiter because filename might contain spaces
            int end = contentDisposition.length();
            int nextSemicolon = contentDisposition.indexOf(';', start);

            if (nextSemicolon != -1) {
                end = nextSemicolon;
            }

            return contentDisposition.substring(start, end).trim();
        }

        return null;
    }

    private static String decodeRFC5987(String encoded) {
        try {
            // This is a simplified decoder - in practice you might want to use URLDecoder
            // but we'll handle the basic percent-encoding manually
            StringBuilder result = new StringBuilder();
            for (int i = 0; i < encoded.length(); i++) {
                char c = encoded.charAt(i);
                if (c == '%' && i + 2 < encoded.length()) {
                    String hex = encoded.substring(i + 1, i + 3);
                    try {
                        int code = Integer.parseInt(hex, 16);
                        result.append((char) code);
                        i += 2; // Skip the next two characters
                    } catch (NumberFormatException e) {
                        result.append(c);
                    }
                } else {
                    result.append(c);
                }
            }
            return result.toString();
        } catch (Exception e) {
            return encoded; // Return as-is if decoding fails
        }
    }

    public static String getExtension(String filename) {
        // Handle null or empty string
        if (filename == null || filename.isEmpty()) {
            return "";
        }

        // Find the last dot
        int lastDotIndex = filename.lastIndexOf('.');

        // If no dot or dot is at the end, return empty string
        if (lastDotIndex == -1 || lastDotIndex == filename.length() - 1) {
            return "";
        }

        // Return the extension in lowercase
        return filename.substring(lastDotIndex + 1).toLowerCase();
    }

    public static Map<String, String> parseContentDisposition(String contentDisposition) {
        Map<String, String> result = new HashMap<>();

        if (contentDisposition == null || contentDisposition.isEmpty()) {
            return result;
        }

        // Split by semicolon
        String[] parts = contentDisposition.split(";");

        // First part is the type
        if (parts.length > 0) {
            result.put("type", parts[0].trim());
        }

        // Process key-value pairs
        for (int i = 1; i < parts.length; i++) {
            String part = parts[i].trim();
            int equalsIndex = part.indexOf('=');

            if (equalsIndex != -1) {
                String key = part.substring(0, equalsIndex).trim().toLowerCase();
                String value = part.substring(equalsIndex + 1).trim();

                // Remove quotes if present
                if (value.startsWith("\"") && value.endsWith("\"")) {
                    value = value.substring(1, value.length() - 1);
                } else if (value.startsWith("'") && value.endsWith("'")) {
                    value = value.substring(1, value.length() - 1);
                }

                result.put(key, value);
            }
        }

        return result;
    }
}

