    }

    @Test
    void testParseContentDispositionWithSingleQuotes() {
        String header = "attachment; filename='document.pdf'";
        Map<String, String> result = FileUploadUtil.parseContentDisposition(header);

        assertEquals("attachment", result.get("type"));
        assertEquals("document.pdf", result.get("filename"));
        assertEquals(2, result.size());
    }

    @Test
    void testParseContentDispositionNoQuotes() {
        String header = "inline; name=test";
        Map<String, String> result = FileUploadUtil.parseContentDisposition(header);

        assertEquals("inline", result.get("type"));
        assertEquals("test", result.get("name"));
        assertEquals(2, result.size());
    }

