class TestStringProcessor {

    @Test
    public void testStringWithMoreThanFiveCharactersGetsReversed() {
        // Test with exactly 7 characters after processing - should be reversed
        // 'abc123test' -> 'abctest' -> 'Abctest' -> 'tsetcbA'
        assertEquals("tsetcbA", StringProcessor.processString("abc123test"));

        // Test with more than 6 characters - should be reversed
        // 'proc123essing456' -> 'processing' -> 'Processing' -> 'gnissecorP'
        assertEquals("gnissecorP", StringProcessor.processString("proc123essing456"));

        // Test with exactly 7 characters - should be reversed
        // 'ver!@#long' -> 'verlong' -> 'Verlong' -> 'gnolreV'
        assertEquals("gnolreV", StringProcessor.processString("ver!@#long"));
    }

    @Test
    public void testStringWithFiveOrFewerCharactersNotReversed() {
        // Test with exactly 5 characters - should NOT be reversed
        // 'h123e456l789l0o' -> 'hello' -> 'Hello'
        assertEquals("Hello", StringProcessor.processString("h123e456l789l0o"));

        // Test with fewer than 5 characters - should NOT be reversed
        // 't!@#e$%^s&*t' -> 'test' -> 'Test'
        assertEquals("Test", StringProcessor.processString("t!@#e$%^s&*t"));

        // Test with 1 character - should NOT be reversed
        // '123a456' -> 'a' -> 'A'
        assertEquals("A", StringProcessor.processString("123a456"));
    }

    @Test
    public void testBoundaryCase() {
        // Test exactly at the boundary - 5 characters should NOT be reversed
        // 'a1b2c3d4e5' -> 'abcde' -> 'Abcde'
        assertEquals("Abcde", StringProcessor.processString("a1b2c3d4e5"));

        // Test just over the boundary - 6 characters should be reversed
        // 'a1b2c3d4e5f6' -> 'abcdef' -> 'Abcdef' -> 'fedcbA'
        assertEquals("fedcbA", StringProcessor.processString("a1b2c3d4e5f6"));
    }
}

