import java.util.*;

class StringProcessor {
    public static String processString(String input) {
        if (input == null || input.isEmpty()) {
            return "Empty";
        }

        StringBuilder lettersOnly = new StringBuilder();
        for (char c : input.toCharArray()) {
            if (Character.isLetter(c)) {
                lettersOnly.append(c);
            }
        }

        String result = lettersOnly.toString();
        if (result.isEmpty()) {
            return "Empty";
        }

        String titleCase = result.substring(0, 1).toUpperCase() + result.substring(1).toLowerCase();

        if (titleCase.length() > 5) {
            result = new StringBuilder(titleCase).reverse().toString();
        } else {
            result = titleCase;
        }

        // Apply length-based markers
        int length = result.length();
        if (length < 3) {
            return "Short_" + result;
        } else if (length >= 3 && length <= 5) {
            return result + "_Medium";
        } else {
            return "Long_" + result + "_Processed";
        }
    }
}

