import java.util.*;

class ChatMessageProcessor {
    private Map<String, List<String>> userMessages;
    private List<String> activeUsers;

    public ChatMessageProcessor() {
        this.userMessages = new HashMap<>();
        this.activeUsers = new ArrayList<>();
    }

    public List<String> userJoins(String username) {
        if (activeUsers.contains(username)) {
            List<String> errorList = new ArrayList<>();
            errorList.add("Error: Username " + username + " is already taken");
            return errorList;
        }
        activeUsers.add(username);
        userMessages.put(username, new ArrayList<>());
        return new ArrayList<>();
    }

    public List<String> userLeaves(String username) {
        if (!activeUsers.contains(username)) {
            List<String> errorList = new ArrayList<>();
            errorList.add("Error: User " + username + " not found");
            return errorList;
        }

        activeUsers.remove(username);
        userMessages.remove(username);

        List<String> notifications = new ArrayList<>();
        for (String user : activeUsers) {
            notifications.add("[Server] - " + username + " has left");
        }

        return notifications;
    }

    public List<String> processMessage(String sender, String message) {
        if (!activeUsers.contains(sender)) {
            List<String> errorList = new ArrayList<>();
            errorList.add("Error: User " + sender + " is not active");
            return errorList;
        }

        List<String> deliveredMessages = new ArrayList<>();
        String formattedMessage = "[" + sender + "] " + message;

        for (String user : activeUsers) {
            if (!user.equals(sender)) {
                deliveredMessages.add(formattedMessage);
            }
        }

        return deliveredMessages;
    }
}

