class NumberComparator {
    public static String analyzeNumbers(List<Integer> numbers) {
        if (numbers == null || numbers.isEmpty()) {
            return "Error: No numbers provided.";
        }

        // Check for null elements
        for (Integer num : numbers) {
            if (num == null) {
                throw new NullPointerException("List must not contain null elements");
            }
        }

        // Check for duplicates
        Set<Integer> uniqueNumbers = new HashSet<>();
        for (Integer num : numbers) {
            if (!uniqueNumbers.add(num)) {
                return "Error: All input numbers must be unique. Found duplicates.";
            }
        }

        // Find largest and smallest numbers
        int largest = numbers.get(0);
        int smallest = numbers.get(0);

        for (Integer num : numbers) {
            if (num > largest) {
                largest = num;
            }
            if (num < smallest) {
                smallest = num;
            }
        }

        // Calculate range
        int range = largest - smallest;

        return "Analysis results:";
    }
}

