import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.*;
import java.util.*;

class TestMessageProducer {
    @Test
    public void testSendMessagesWithRetries() throws InterruptedException {
        // Create a queue with capacity 1 to force retries
        ArrayBlockingQueue<String> queue = new ArrayBlockingQueue<>(1);
        queue.offer("existing message"); // Fill the queue

        MessageProducer producer = new MessageProducer(queue, 2, 50);

        // Start a thread to remove the blocking message after a delay
        Thread remover = new Thread(() -> {
            try {
                Thread.sleep(100); // Wait a bit for the send to start
                queue.take(); // Remove the blocking message
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        });
        remover.start();

        // This should fail to add immediately, but succeed after retries
        producer.sendMessages("test", 1);

        // Wait for remover to finish
        remover.join(200);

        // Check that the message was eventually added
        assertEquals(1, queue.size());
        assertTrue(queue.contains("test 1"));

        // Check that sentMessages was updated
        assertEquals(1, producer.getSentMessages().size());
        assertTrue(producer.getSentMessages().contains("test 1"));
    }

    @Test
    public void testSendMessagesWithExceededRetries() throws InterruptedException {
        // Create a queue with very small capacity that will be filled
        ArrayBlockingQueue<String> queue = new ArrayBlockingQueue<>(1);
        queue.offer("blocking_message"); // Fill the queue

        MessageProducer producer = new MessageProducer(queue, 1, 50);

        // This should fail to add even after retries
        producer.sendMessages("test", 1);

        // Check that no new messages were added
        assertEquals(1, queue.size());
        assertTrue(queue.contains("blocking_message"));
        assertFalse(queue.contains("test 1"));

        // Check that sentMessages was not updated
        assertEquals(0, producer.getSentMessages().size());
    }
}

