import java.util.*;
import java.util.concurrent.*;

class MessageProducer {
    private BlockingQueue<String> messageQueue;
    private List<String> sentMessages;
    private int maxRetries;
    private long retryDelay;

    public MessageProducer() {
        this(new LinkedBlockingQueue<String>(), 3, 100);
    }

    public MessageProducer(BlockingQueue<String> messageQueue, int maxRetries, long retryDelayMillis) {
        this.messageQueue = messageQueue;
        this.sentMessages = new ArrayList<>();
        this.maxRetries = maxRetries;
        this.retryDelay = retryDelayMillis;
    }

    public void sendMessages(String prefix, int count) throws InterruptedException {
        sentMessages.clear();

        for (int i = 1; i <= count; i++) {
            String message = prefix + " " + i;
            boolean added = false;
            int attempts = 0;

            while (!added && attempts <= maxRetries) {
                if (attempts > 0) {
                    try {
                        Thread.sleep(retryDelay);
                    } catch (InterruptedException e) {
                        Thread.currentThread().interrupt();
                        throw e;
                    }
                }

                try {
                    added = messageQueue.offer(message, 100, TimeUnit.MILLISECONDS);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                    throw e;
                }
                attempts++;
            }

            if (added) {
                sentMessages.add(message);
            } else {
                System.err.println("Failed to send message after " + attempts + " attempts: " + message);
            }
        }
    }

    public BlockingQueue<String> getMessageQueue() {
        return messageQueue;
    }

    public List<String> getSentMessages() {
        return new ArrayList<>(sentMessages);
    }
}

