import java.util.*;
import java.util.concurrent.*;

class TestMessageProducer {

    @Test
    @Timeout(5)
    public void testInterruptedExceptionDuringSleep() throws InterruptedException {
        BlockingQueue<String> queue = new LinkedBlockingQueue<>(1);
        MessageProducer producer = new MessageProducer(queue, 3, 1000); // 1 second retry delay

        // Fill the queue so messages can't be added immediately
        queue.put("existing message");

        Thread testThread = new Thread(() -> {
            try {
                producer.sendMessages("test", 1);
                fail("Expected InterruptedException");
            } catch (InterruptedException e) {
                // Expected
            }
        });

        testThread.start();

        // Interrupt the thread while it's sleeping during retry
        Thread.sleep(100); // Let it start and enter the retry sleep
        testThread.interrupt();
        testThread.join();

        // Verify the thread's interrupt status is preserved
        assertTrue(testThread.isInterrupted());
    }

    @Test
    @Timeout(5)
    public void testInterruptedExceptionDuringQueueOffer() throws InterruptedException {
        // Create a custom BlockingQueue that throws InterruptedException when interrupted
        BlockingQueue<String> interruptingQueue = new LinkedBlockingQueue<String>() {
            @Override
            public boolean offer(String e, long timeout, TimeUnit unit) throws InterruptedException {
                // Check if the current thread is interrupted and throw if so
                if (Thread.currentThread().isInterrupted()) {
                    throw new InterruptedException("Interrupted during offer");
                }
                // Otherwise behave normally for this test
                return super.offer(e, timeout, unit);
            }
        };

        MessageProducer producer = new MessageProducer(interruptingQueue, 0, 0);

        Thread testThread = new Thread(() -> {
            try {
                producer.sendMessages("test", 1);
                fail("Expected InterruptedException");
            } catch (InterruptedException e) {
                // Expected
            }
        });

        testThread.start();

        // Interrupt the thread before the queue operation
        testThread.interrupt();
        testThread.join();

        // Verify the thread's interrupt status is preserved
        assertTrue(testThread.isInterrupted());
    }
}

