turn_datas.solution:
class BSTNode {
    int value;
    BSTNode left;
    BSTNode right;

    public BSTNode(int value) {
        this.value = value;
    }
}

class BSTValidator {
    public static boolean isValidBST(BSTNode root) {
        return isValidBSTHelper(root, Integer.MIN_VALUE, Integer.MAX_VALUE);
    }

    private static boolean isValidBSTHelper(BSTNode node, int minVal, int maxVal) {
        // Empty tree is valid
        if (node == null) {
            return true;
        }

        // Check if current node violates BST property
        if (node.value < minVal || node.value > maxVal) {
            return false;
        }

        // Recursively validate left and right subtrees
        // Left subtree: all values must be <= current node's value
        // Right subtree: all values must be > current node's value
        return isValidBSTHelper(node.left, minVal, node.value) &&
               isValidBSTHelper(node.right, node.value + 1, maxVal);
    }
}

