class BSTNode {
    int value;
    BSTNode left;
    BSTNode right;

    public BSTNode(int value) {
        this.value = value;
    }
}

class BSTValidator {
    public static boolean isValidBST(BSTNode root) {
        return isValidBST(root, null, null);
    }

    private static boolean isValidBST(BSTNode node, Integer lower, Integer upper) {
        // Empty tree is valid
        if (node == null) {
            return true;
        }

        // Check if current node violates BST property
        if (lower != null && node.value <= lower) {
            return false;
        }
        if (upper != null && node.value >= upper) {
            return false;
        }

        // Recursively validate left and right subtrees
        // Left subtree: all values must be < current node's value
        // Right subtree: all values must be > current node's value
        return isValidBST(node.left, lower, node.value) &&
               isValidBST(node.right, node.value, upper);
    }
}

