    @Test
    public void testIntegerMinValueConstraintHandling() {
        // Previous implementation used node.value + 1 for right subtree lower bound
        // When node.value = Integer.MIN_VALUE, this becomes Integer.MIN_VALUE + 1 = -2147483647
        // Current implementation passes node.value directly as lower bound
        // This creates different constraint behavior
        BSTNode root = new BSTNode(0);
        root.left = new BSTNode(Integer.MIN_VALUE);
        root.left.right = new BSTNode(Integer.MIN_VALUE); // Should be invalid: not > parent
        assertFalse(BSTValidator.isValidBST(root));
