import java.awt.*;
import java.util.List;

class EnhancedLayoutManager {
    private List<Component> components;
    private List<LayoutConstraint> constraints;
    private boolean respectAspectRatio;
    private double aspectRatio;

    public EnhancedLayoutManager(List<Component> components, List<LayoutConstraint> constraints,
                               boolean respectAspectRatio, double aspectRatio) {
        this.components = components;
        this.constraints = constraints;
        this.respectAspectRatio = respectAspectRatio;
        this.aspectRatio = aspectRatio;
    }

    public java.util.Map<Component, Rectangle> calculateLayout(int containerWidth, int containerHeight) {
        java.util.Map<Component, Rectangle> result = new java.util.HashMap<>();

        int totalFixedWidth = 0;
        int totalFixedHeight = 0;

        for (int i = 0; i < components.size(); i++) {
            Component component = components.get(i);
            LayoutConstraint constraint = constraints.get(i);

            if (constraint.isFixedSize()) {
                Rectangle rect = new Rectangle(constraint.getX(), constraint.getY(), 
                                             constraint.getWidth(), constraint.getHeight());
                result.put(component, rect);
                totalFixedWidth += constraint.getWidth();
                totalFixedHeight += constraint.getHeight();
            }
        }

        int remainingWidth = containerWidth - totalFixedWidth;
        int remainingHeight = containerHeight - totalFixedHeight;

        // Calculate total weight of flexible components
        double totalWeight = 0.0;
        for (int i = 0; i < components.size(); i++) {
            LayoutConstraint constraint = constraints.get(i);
            if (!constraint.isFixedSize()) {
                totalWeight += constraint.getWeight();
            }
        }

        // Distribute remaining space among flexible components
        for (int i = 0; i < components.size(); i++) {
            Component component = components.get(i);
            LayoutConstraint constraint = constraints.get(i);

            if (!constraint.isFixedSize()) {
                int flexWidth = (int) (remainingWidth * constraint.getWeight() / totalWeight);
                int flexHeight = (int) (remainingHeight * constraint.getWeight() / totalWeight);
                Rectangle rect = new Rectangle(constraint.getX(), constraint.getY(), flexWidth, flexHeight);
                result.put(component, rect);
            }
        }

        return result;
    }

    public static class LayoutConstraint {
        private int x;
        private int y;
        private int width;
        private int height;
        private double weight;
        private boolean fixedSize;

        public LayoutConstraint(int x, int y, int width, int height, double weight, boolean fixedSize) {
            this.x = x;
            this.y = y;
            this.width = width;
            this.height = height;
            this.weight = weight;
            this.fixedSize = fixedSize;
        }

        public int getX() { return x; }
        public int getY() { return y; }
        public int getWidth() { return width; }
        public int getHeight() { return height; }
        public double getWeight() { return weight; }
        public boolean isFixedSize() { return fixedSize; }
    }
}

