        Map<Component, Rectangle> layout = manager.calculateLayout(600, 400);

        // Total weight = 2 + 3 + 1 = 6
        // Remaining width = 600, remaining height = 400
        // comp1: width = 600 * 2/6 = 200, height = 400 * 2/6 = 133
        // comp2: width = 600 * 3/6 = 300, height = 400 * 3/6 = 200
