import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.awt.*;
import java.util.*;

class TestEnhancedLayoutManager {

    @Test
    public void testAspectRatioAdjustmentReducesWidth() {
        // Test case where current ratio > target ratio, so width should be reduced
        Component comp1 = new Component() {};
        EnhancedLayoutManager.LayoutConstraint constraint1 = 
            new EnhancedLayoutManager.LayoutConstraint(0, 0, 0, 0, 1.0, false);

        EnhancedLayoutManager manager = new EnhancedLayoutManager(
            Arrays.asList(comp1), Arrays.asList(constraint1), true, 1.5); // aspectRatio = 1.5

        Map<Component, Rectangle> layout = manager.calculateLayout(300, 100); // gives 300x100 to flexible component
        Rectangle rect = layout.get(comp1);

        // Current ratio would be 300/100 = 3.0, which > 1.5
        // So width should be reduced to height * aspectRatio = 100 * 1.5 = 150
        assertEquals(150, rect.width);
        assertEquals(100, rect.height);
    }

    @Test
    public void testAspectRatioAdjustmentReducesHeight() {
        // Test case where current ratio < target ratio, so height should be reduced
        Component comp1 = new Component() {};
        EnhancedLayoutManager.LayoutConstraint constraint1 = 
            new EnhancedLayoutManager.LayoutConstraint(0, 0, 0, 0, 1.0, false);

        EnhancedLayoutManager manager = new EnhancedLayoutManager(
            Arrays.asList(comp1), Arrays.asList(constraint1), true, 2.0); // aspectRatio = 2.0

        Map<Component, Rectangle> layout = manager.calculateLayout(100, 100); // gives 100x100 to flexible component
        Rectangle rect = layout.get(comp1);

        // Current ratio would be 100/100 = 1.0, which < 2.0
        // So height should be reduced to width / aspectRatio = 100 / 2.0 = 50
        assertEquals(100, rect.width);
        assertEquals(50, rect.height);
    }

    @Test
    public void testAspectRatioPreservesPosition() {
        // Test that position is preserved during aspect ratio adjustment
        Component comp1 = new Component() {};
        EnhancedLayoutManager.LayoutConstraint constraint1 = 
            new EnhancedLayoutManager.LayoutConstraint(10, 20, 0, 0, 1.0, false);

        EnhancedLayoutManager manager = new EnhancedLayoutManager(
            Arrays.asList(comp1), Arrays.asList(constraint1), true, 1.0);

        Map<Component, Rectangle> layout = manager.calculateLayout(200, 100);
        Rectangle rect = layout.get(comp1);

        assertEquals(10, rect.x);
        assertEquals(20, rect.y);
    }

    @Test
    public void testAspectRatioOnlyAffectsFlexibleComponents() {
        // Test that fixed-size components are not affected by aspect ratio
        Component comp1 = new Component() {}; // fixed
        Component comp2 = new Component() {}; // flexible

        EnhancedLayoutManager.LayoutConstraint constraint1 = 
            new EnhancedLayoutManager.LayoutConstraint(0, 0, 50, 25, 0.0, true);
        EnhancedLayoutManager.LayoutConstraint constraint2 = 
            new EnhancedLayoutManager.LayoutConstraint(0, 0, 0, 0, 1.0, false);

        EnhancedLayoutManager manager = new EnhancedLayoutManager(
            Arrays.asList(comp1, comp2), Arrays.asList(constraint1, constraint2), true, 1.0);

        Map<Component, Rectangle> layout = manager.calculateLayout(300, 200);

        // Fixed component should remain unchanged
        Rectangle rect1 = layout.get(comp1);
        assertEquals(50, rect1.width);
        assertEquals(25, rect1.height);

        // Flexible component should be adjusted for aspect ratio
        Rectangle rect2 = layout.get(comp2);
        // Remaining space: 250x175, ratio = 250/175  1.43 > 1.0
        // So width should be reduced to height * 1.0 = 175
        assertEquals(175, rect2.width);
        assertEquals(175, rect2.height);
    }
}

