        }

        List<T> workingData = new ArrayList<>(data);

        if (shouldSort) {
            Collections.sort(workingData);
            loadBalanced(tree, workingData, 0, workingData.size() - 1);
        } else {
            for (T item : workingData) {
                tree.insert(item);
            }
