import java.util.*;

class AuthService {
    public Map<String, Object> processOAuth2User(String provider, 
                                               Map<String, Object> userAttributes,
                                               Map<String, User> existingUsers,
                                               Set<Role> availableRoles) {
        // Input validation
        if (provider == null || provider.isEmpty()) {
            throw new IllegalArgumentException("Provider cannot be null or empty");
        }
        if (userAttributes == null || userAttributes.isEmpty()) {
            throw new IllegalArgumentException("User attributes cannot be null or empty");
        }
        if (!userAttributes.containsKey("email")) {
            throw new IllegalArgumentException("Email is required in user attributes");
        }

        String email = (String) userAttributes.get("email");
        User user;
        String status;

        if (existingUsers.containsKey(email)) {
            user = existingUsers.get(email);
            status = "authenticated";
        } else {
            user = registerNewUser(userAttributes, availableRoles);
            existingUsers.put(email, user);
            status = "registered";
        }

        Map<String, Object> result = new HashMap<>();
        result.put("status", status);
        result.put("user", user);
        result.put("provider", provider);

        return result;
    }

    private User registerNewUser(Map<String, Object> userAttributes, Set<Role> availableRoles) {
        String email = (String) userAttributes.get("email");
        String username = email.split("@")[0] + "_oauth2";

        String givenName = (String) userAttributes.getOrDefault("given_name", "");
        String familyName = (String) userAttributes.getOrDefault("family_name", "");

        User user = new User(email, username);
        user.setName(givenName);
        user.setLastname(familyName);
        user.setEnabled(true);

        // Assign default ROLE_USER
        for (Role role : availableRoles) {
            if ("ROLE_USER".equals(role.getName())) {
                user.setRole(role);
                break;
            }
        }

        return user;
    }
}

