import java.util.HashMap;
import java.util.Map;
import java.util.UUID;
import java.util.regex.Pattern;

class UserManager {
    private Map<String, User> userDatabase;
    private Map<String, String> activeSessions;
    private static final String EMAIL_PATTERN = 
        "^[a-zA-Z0-9_+&*-]+(?:\\.[a-zA-Z0-9_+&*-]+)*@(?:[a-zA-Z0-9-]+\\.)+[a-zA-Z]{2,7}$";

    public UserManager() {
        this.userDatabase = new HashMap<>();
        this.activeSessions = new HashMap<>();
    }

    public Map<String, Object> registerUser(String username, String password, String email) {
        Map<String, Object> result = new HashMap<>();

        // Check if username is already taken
        if (userDatabase.containsKey(username)) {
            result.put("success", false);
            result.put("message", "Username already taken");
            return result;
        }

        // Check password length
        if (password.length() < 8) {
            result.put("success", false);
            result.put("message", "Password must be at least 8 characters long");
            return result;
        }

        // Validate email format
        if (!isValidEmail(email)) {
            result.put("success", false);
            result.put("message", "Invalid email format");
            return result;
        }

        // Hash the password using hashCode()
        int hashedPassword = password.hashCode();

        // Create new user with hashed password
        User newUser = new User(username, String.valueOf(hashedPassword), email);
        userDatabase.put(username, newUser);

        result.put("success", true);
        result.put("message", "User registered successfully");
        return result;
    }

    public Map<String, Object> loginUser(String username, String password) {
        Map<String, Object> result = new HashMap<>();

        // Check if username exists
        if (!userDatabase.containsKey(username)) {
            result.put("success", false);
            result.put("message", "Invalid username or password");
            return result;
        }

        // Get user
        User user = userDatabase.get(username);

        // Hash input password and check if it matches
        int hashedInputPassword = password.hashCode();
        if (!user.getPassword().equals(String.valueOf(hashedInputPassword))) {
            result.put("success", false);
            result.put("message", "Invalid username or password");
            return result;
        }

        // Generate UUID session token
        String sessionToken = UUID.randomUUID().toString();

        // Store session token in activeSessions
        activeSessions.put(sessionToken, username);

        // Return success result
        result.put("success", true);
        result.put("sessionToken", sessionToken);
        result.put("user", user);
        return result;
    }

    public Map<String, Object> logoutUser(String sessionToken) {
        Map<String, Object> result = new HashMap<>();

        if (activeSessions.containsKey(sessionToken)) {
            activeSessions.remove(sessionToken);
            result.put("success", true);
            result.put("message", "User logged out successfully");
        } else {
            result.put("success", false);
            result.put("message", "Invalid session token");
        }

        return result;
    }

    private boolean isValidEmail(String email) {
        return Pattern.matches(EMAIL_PATTERN, email);
    }
}

class User {
    private String username;
    private String password;
    private String email;

    public User(String username, String password, String email) {
        this.username = username;
        this.password = password;
        this.email = email;
    }

    public String getUsername() {
        return username;
    }

    public String getPassword() {
        return password;
    }

    public String getEmail() {
        return email;
    }
}

