    void testLogoutUser() {
        // Register and login a user to get a session token
        userManager.registerUser("testuser", "password123", "test@example.com");
        Map<String, Object> loginResult = userManager.loginUser("testuser", "password123");
        String sessionToken = (String) loginResult.get("sessionToken");

        // Test valid logout
        Map<String, Object> logoutResult = userManager.logoutUser(sessionToken);
        assertTrue((Boolean) logoutResult.get("success"), "Logout should succeed with valid session token");
        assertEquals("User logged out successfully", logoutResult.get("message"), "Should return success message");

        // Test invalid logout
        Map<String, Object> invalidLogoutResult = userManager.logoutUser("invalid_token");
        assertFalse((Boolean) invalidLogoutResult.get("success"), "Logout should fail with invalid session token");
        assertEquals("Invalid session token", invalidLogoutResult.get("message"), "Should return error message for invalid token");
    }

    @Test
    void testIsValidEmail() throws Exception {
        // Use reflection to access the private isValidEmail method
        Method method = UserManager.class.getDeclaredMethod("isValidEmail", String.class);
        method.setAccessible(true);

        // Test valid emails
        assertTrue((Boolean) method.invoke(userManager, "test@example.com"));
        assertTrue((Boolean) method.invoke(userManager, "user.name+tag@example.com"));
        assertTrue((Boolean) method.invoke(userManager, "user123@test-domain.co.uk"));

        // Test invalid emails
        assertFalse((Boolean) method.invoke(userManager, "invalid.email"));
        assertFalse((Boolean) method.invoke(userManager, "@example.com"));
        assertFalse((Boolean) method.invoke(userManager, "user@"));
        assertFalse((Boolean) method.invoke(userManager, "user@.com"));
        assertFalse((Boolean) method.invoke(userManager, "user name@example.com"));
    }
}

