import java.util.Calendar;

class DateUtils {
    private static final String[] DAYS_OF_WEEK = {
        "SUNDAY", "MONDAY", "TUESDAY", "WEDNESDAY", "THURSDAY", "FRIDAY", "SATURDAY"
    };

    public static Map<String, String> analyzeDate(int day, int month, int year) {
        if (!isValidDate(day, month, year)) {
            throw new IllegalArgumentException("Invalid date");
        }
        Map<String, String> result = new HashMap<>();
        result.put("isLeapYear", isLeapYear(year) ? "Yes" : "No");
        result.put("daysInMonth", String.valueOf(getDaysInMonth(month, year)));
        result.put("zodiacSign", getZodiacSign(day, month));
        result.put("dayOfWeek", getDayOfWeek(day, month, year));
        return result;
    }

    public static boolean isValidDate(int day, int month, int year) {
        // Check year range
        if (year < 1) {
            return false;
        }

        // Check month range
        if (month < 1 || month > 12) {
            return false;
        }

        // Check day range based on month and leap year
        int daysInMonth = getDaysInMonth(month, year);
        if (day < 1 || day > daysInMonth) {
            return false;
        }

        return true;
    }

    public static boolean isLeapYear(int year) {
        return (year % 4 == 0) && (year % 100 != 0 || year % 400 == 0);
    }

    public static int getDaysInMonth(int month, int year) {
        switch (month) {
            case 1: case 3: case 5: case 7: case 8: case 10: case 12:
                return 31;
            case 4: case 6: case 9: case 11:
                return 30;
            case 2:
                return isLeapYear(year) ? 29 : 28;
            default:
                return 0; // Should not happen due to earlier validation
        }
    }

    public static String getZodiacSign(int day, int month) {
        if ((month == 3 && day >= 21) || (month == 4 && day <= 19)) {
            return "Aries";
        } else if ((month == 4 && day >= 20) || (month == 5 && day <= 20)) {
            return "Taurus";
        } else if ((month == 5 && day >= 21) || (month == 6 && day <= 20)) {
            return "Gemini";
        } else if ((month == 6 && day >= 21) || (month == 7 && day <= 22)) {
            return "Cancer";
        } else if ((month == 7 && day >= 23) || (month == 8 && day <= 22)) {
            return "Leo";
        } else if ((month == 8 && day >= 23) || (month == 9 && day <= 22)) {
            return "Virgo";
        } else if ((month == 9 && day >= 23) || (month == 10 && day <= 22)) {
            return "Libra";
        } else if ((month == 10 && day >= 23) || (month == 11 && day <= 21)) {
            return "Scorpio";
        } else if ((month == 11 && day >= 22) || (month == 12 && day <= 21)) {
            return "Sagittarius";
        } else if ((month == 12 && day >= 22) || (month == 1 && day <= 19)) {
            return "Capricorn";
        } else if ((month == 1 && day >= 20) || (month == 2 && day <= 18)) {
            return "Aquarius";
        } else if ((month == 2 && day >= 19) || (month == 3 && day <= 20)) {
            return "Pisces";
        }
        return "Unknown"; // Should not happen with valid dates
    }

    public static String getDayOfWeek(int day, int month, int year) {
        Calendar calendar = Calendar.getInstance();
        calendar.set(year, month - 1, day); // month is 0-based in Calendar
        int dayOfWeek = calendar.get(Calendar.DAY_OF_WEEK);
        return DAYS_OF_WEEK[dayOfWeek - 1]; // Calendar.DAY_OF_WEEK is 1-based (Sunday=1)
    }
}

