import java.util.*;

class DataMatcher {
    public Map<String, List<String[]>> enhancedMatch(List<String[]> dataset1, List<String[]> dataset2, int matchStrategy, int[] keyIndices) {
        // Input validation
        if (dataset1 == null || dataset2 == null) {
            throw new IllegalArgumentException("Datasets cannot be null");
        }
        if (matchStrategy < 1 || matchStrategy > 3) {
            throw new IllegalArgumentException("Match strategy must be 1, 2, or 3");
        }
        if (keyIndices == null || keyIndices.length != 2) {
            throw new IllegalArgumentException("Key indices must be non-null and have exactly two elements");
        }

        Map<String, List<String[]>> result = new HashMap<>();
        List<String[]> matched = new ArrayList<>();
        List<String[]> unmatched1 = new ArrayList<>(dataset1);
        List<String[]> unmatched2 = new ArrayList<>(dataset2);

        result.put("matched", matched);
        result.put("unmatched1", unmatched1);
        result.put("unmatched2", unmatched2);

        if (matchStrategy == 1) {
            // Exact matching logic
            Map<String, String[]> dataset2Map = new HashMap<>();
            for (String[] record2 : dataset2) {
                String key = generateKey(record2, new int[]{keyIndices[1]}); // Use second index for dataset2
                dataset2Map.put(key, record2);
            }

            Iterator<String[]> iter1 = unmatched1.iterator();
            while (iter1.hasNext()) {
                String[] record1 = iter1.next();
                String key = generateKey(record1, new int[]{keyIndices[0]}); // Use first index for dataset1

                if (dataset2Map.containsKey(key)) {
                    String[] record2 = dataset2Map.get(key);

                    String[] combined = new String[record1.length + record2.length];
                    System.arraycopy(record1, 0, combined, 0, record1.length);
                    System.arraycopy(record2, 0, combined, record1.length, record2.length);

                    matched.add(combined);
                    iter1.remove();
                    unmatched2.remove(record2);
                }
            }
        } else if (matchStrategy == 2) {
            // Contains matching logic (case-sensitive)
            Iterator<String[]> iter1 = unmatched1.iterator();
            while (iter1.hasNext()) {
                String[] record1 = iter1.next();
                String key1 = generateKey(record1, new int[]{keyIndices[0]}); // Use first index for dataset1

                Iterator<String[]> iter2 = unmatched2.iterator();
                while (iter2.hasNext()) {
                    String[] record2 = iter2.next();
                    String key2 = generateKey(record2, new int[]{keyIndices[1]}); // Use second index for dataset2

                    if (key1.contains(key2) || key2.contains(key1)) {
                        String[] combined = new String[record1.length + record2.length];
                        System.arraycopy(record1, 0, combined, 0, record1.length);
                        System.arraycopy(record2, 0, combined, record1.length, record2.length);

                        matched.add(combined);
                        iter1.remove();
                        iter2.remove();
                        break; // Move to next record in dataset1
                    }
                }
            }
        } else if (matchStrategy == 3) {
            // Fuzzy matching logic (case-insensitive contains)
            Iterator<String[]> iter1 = unmatched1.iterator();
            while (iter1.hasNext()) {
                String[] record1 = iter1.next();
                String key1 = generateKey(record1, new int[]{keyIndices[0]}).toLowerCase(); // Use first index for dataset1

                Iterator<String[]> iter2 = unmatched2.iterator();
                while (iter2.hasNext()) {
                    String[] record2 = iter2.next();
                    String key2 = generateKey(record2, new int[]{keyIndices[1]}).toLowerCase(); // Use second index for dataset2

                    if (key1.contains(key2) || key2.contains(key1)) {
                        String[] combined = new String[record1.length + record2.length];
                        System.arraycopy(record1, 0, combined, 0, record1.length);
                        System.arraycopy(record2, 0, combined, record1.length, record2.length);

                        matched.add(combined);
                        iter1.remove();
                        iter2.remove();
                        break; // Move to next record in dataset1
                    }
                }
            }
        }

        return result;
    }

    private String generateKey(String[] record, int[] keyIndices) {
        StringBuilder keyBuilder = new StringBuilder();
        for (int i = 0; i < keyIndices.length; i++) {
            int index = keyIndices[i];
            if (index < record.length) {
                keyBuilder.append(record[index].trim());
            }
            if (i < keyIndices.length - 1) {
                keyBuilder.append("|"); // Use a delimiter to separate key parts
            }
        }
        return keyBuilder.toString();
    }
}

