            new String[]{"D", "E"}
        );

        List<String[]> dataset2 = Arrays.asList(
            new String[]{"1", "2", "3", "4"},
            new String[]{"5", "6"}
        );

        // No matches expected
        Map<String, List<String[]>> result = matcher.enhancedMatch(dataset1, dataset2, 1, new int[]{0, 0});

        List<String[]> unmatched1 = result.get("unmatched1");
        List<String[]> unmatched2 = result.get("unmatched2");

        // Check that all records in unmatched1 have length 3 (max length in dataset1)
        for (String[] record : unmatched1) {
            assertEquals(3, record.length);
        }

        // Check that all records in unmatched2 have length 4 (max length in dataset2)
        for (String[] record : unmatched2) {
            assertEquals(4, record.length);
        }

