import java.util.*;

class EnhancedQueue<E> {
    private Node<E> front;
    private Node<E> rear;
    private int size;
    private final int capacity;
    private static final int DEFAULT_CAPACITY = 10;

    private static class Node<E> {
        E data;
        Node<E> next;

        Node(E data) {
            this.data = data;
            this.next = null;
        }
    }

    public EnhancedQueue() {
        this(DEFAULT_CAPACITY);
    }

    public EnhancedQueue(int capacity) {
        if (capacity <= 0) {
            throw new IllegalArgumentException("Capacity must be greater than zero");
        }
        this.front = null;
        this.rear = null;
        this.size = 0;
        this.capacity = capacity;
    }

    public void enqueue(E newData) {
        if (size >= capacity) {
            throw new IllegalStateException("Queue is full");
        }
        Node<E> newNode = new Node<>(newData);
        if (rear != null) {
            rear.next = newNode;
        }
        rear = newNode;
        if (front == null) {
            front = rear;
        }
        size++;
    }

    public E dequeue() {
        if (isEmpty()) {
            throw new NoSuchElementException("Queue is empty");
        }
        E data = front.data;
        front = front.next;
        if (front == null) {
            rear = null;
        }
        size--;
        return data;
    }

    public E peek() {
        if (isEmpty()) {
            throw new NoSuchElementException("Queue is empty");
        }
        return front.data;
    }

    public boolean isEmpty() {
        return front == null;
    }

    public int size() {
        return size;
    }
}

