    void testEnqueueBeyondCapacity() {
        EnhancedQueue<Integer> queue = new EnhancedQueue<>(2);
        queue.enqueue(1);
        queue.enqueue(2);
        assertThrows(IllegalStateException.class, () -> queue.enqueue(3));
    }
}

