    void testClearOnEmptyQueue() {
        EnhancedQueue<Integer> queue = new EnhancedQueue<>(3);
        assertTrue(queue.isEmpty());

        queue.clear(); // Should not throw

        assertTrue(queue.isEmpty());
        assertEquals(0, queue.size());
    }
}

