import java.util.HashMap;
import java.util.Map;
import java.util.regex.Pattern;

class PostCreator {
    public Map<String, Object> createPost(
            String title, String description, String price,
            String country, String stateProvince, String city,
            String contactEmail, Object imageData) {
        Map<String, Object> result = new HashMap<>();
        Map<String, String> errors = new HashMap<>();

        // Validate title
        if (title == null || title.isEmpty()) {
            errors.put("title", "Title is required");
        } else if (title.length() > 100) {
            errors.put("title", "Title must be at most 100 characters long");
        }

        // Validate description
        if (description == null || description.isEmpty()) {
            errors.put("description", "Description is required");
        }

        // Validate price if provided
        if (price != null && !price.isEmpty()) {
            try {
                double priceValue = Double.parseDouble(price);
                if (priceValue < 0) {
                    errors.put("price", "Price must be a non-negative number");
                }
            } catch (NumberFormatException e) {
                errors.put("price", "Price must be a valid number");
            }
        }

        // Validate contactEmail if provided
        if (contactEmail != null && !contactEmail.isEmpty()) {
            String emailPattern = "^[A-Za-z0-9+_.-]+@(.+)$";
            if (!Pattern.matches(emailPattern, contactEmail)) {
                errors.put("contactEmail", "Invalid email format");
            }
        }

        // Validate imageData
        if (imageData == null) {
            errors.put("imageData", "Image data is required");
        } else if (!(imageData instanceof String || imageData instanceof byte[])) {
            errors.put("imageData", "Image data must be a String or byte array");
        }

        if (!errors.isEmpty()) {
            result.put("success", false);
            result.put("errors", errors);
            return result;
        }

        result.put("success", true);

        Map<String, Object> postData = new HashMap<>();
        postData.put("title", title);
        postData.put("description", description);
        postData.put("price", price);
        postData.put("country", country);
        postData.put("stateProvince", stateProvince);
        postData.put("city", city);
        postData.put("contactEmail", contactEmail);
        postData.put("imageData", imageData);

        result.put("postData", postData);

        // Add imageType based on imageData type
        if (imageData instanceof String) {
            result.put("imageType", "path");
        } else if (imageData instanceof byte[]) {
            result.put("imageType", "bytes");
        }

        return result;
    }
}

