class RedditFeedAnalyzer {
    public String analyzeAndFormatFeed(List<RedditFeedItem> feedItems, int minScore) {
        if (feedItems == null || feedItems.isEmpty()) {
            return "No feed items to analyze";
        }

        List<RedditFeedItem> filteredItems = new ArrayList<>();
        long totalScore = 0;
        long totalComments = 0;

        for (RedditFeedItem item : feedItems) {
            if (item.getScore() >= minScore) {
                filteredItems.add(item);
                totalScore += item.getScore();
                totalComments += item.getNumComments();
            }
        }

        int count = filteredItems.size();
        double averageScore = count > 0 ? (double) totalScore / count : 0.0;
        double averageComments = count > 0 ? (double) totalComments / count : 0.0;

        String summary = "Feed Analysis Summary: Total items: " + count;
        summary += "\nAverage score: " + String.format("%.2f", averageScore);
        summary += "\nAverage comments: " + String.format("%.2f", averageComments);

        // Sort filtered items by score in descending order
        filteredItems.sort((a, b) -> Integer.compare(b.getScore(), a.getScore()));

        // Add top 3 items section
        summary += "\nTop 3 items by score:";
