    void testSendMessageSuccess() {
        MessageSystemSimulator simulator = new MessageSystemSimulator();
        TestHandler senderHandler = new TestHandler();
        TestHandler receiverHandler = new TestHandler();

        String senderAddress = simulator.registerService("sender", senderHandler);
        String receiverAddress = simulator.registerService("receiver", receiverHandler);

